import React, { useState, useEffect } from 'react';
import './css/LoginForm.css'; 

function ConfirmEmail({ onLogin }) {
    const [email, setEmail] = useState('');
    const [organizations, setOrganizations] = useState([]);
    const [selectedOrg, setSelectedOrg] = useState('');
    const [emailSuffixes, setEmailSuffixes] = useState([]);
    const [selectedEmailSuffix, setSelectedEmailSuffix] = useState('');
    const [verificationCode, setVerificationCode] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        fetch('/users/organization/list')
            .then(response => response.json())
            .then(data => {
                if(data.status === 0) {
                    setOrganizations(data.organizations);
                } else {
                    setError(data.error_msg);
                }
            });
    }, []);

    const handleOrgSelect = (event) => {
        const orgId = event.target.value;
        setSelectedOrg(orgId);
        fetch(`/users/organization/emails`, {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({ org_id: orgId })
        })
        .then(response => response.json())
        .then(data => {
            if(data.status === 0) {
                setEmailSuffixes(data.emails);
            } else {
                setError(data.error_msg);
            }
        });
    };

    const handleEmailSuffixSelect = (event) => {
        setSelectedEmailSuffix(event.target.value);
    };

    const handleVerificationRequest = () => {
        fetch('/users/organization/send_auth_email', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                organization_id: selectedOrg,
                email: email + selectedEmailSuffix,
                id: "user_id" // This should be dynamically set if required
            })
        })
        .then(response => response.json())
        .then(data => {
            if(data.status !== 0) {
                setError(data.error_msg);
            }
        });
    };

    const verifyCode = () => {
        fetch('/users/organization/check_auth_code', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                email: email + selectedEmailSuffix,
                code: verificationCode,
                id: "user_id" // This should also be dynamically set
            })
        })
        .then(response => response.json())
        .then(data => {
            if(data.status === 0) {
                onLogin(); // or navigate to another page or dashboard
            } else {
                setError(data.error_msg);
            }
        });
    };

    return (
        <div className="login-container">
            <div className="title-container">
                <h1 className="title">Everytime</h1>
                <p className="subtitle">for Foreign</p>
            </div>
            <div>
                <h3 style={{ textAlign: 'center', margin: 0 }}>회원가입을 진행해주세요!</h3>
                <h5 style={{ marginTop: 0 }}>학교 이메일을 통해서 회원가입을 하실 수 있습니다!</h5>
            </div>
            <div>
                <select onChange={handleOrgSelect} value={selectedOrg}>
                    <option value="">Select your organization</option>
                    {organizations.map((org) => (
                        <option key={org.id} value={org.id}>{org.name} - {org.region}</option>
                    ))}
                </select>
                <select onChange={handleEmailSuffixSelect} value={selectedEmailSuffix}>
                    <option value="">Select your email suffix</option>
                    {emailSuffixes.map((suffix, index) => (
                        <option key={index} value={suffix}>{suffix}</option>
                    ))}
                </select>
                <input
                    type="text"
                    placeholder="Enter your email local part"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <button onClick={handleVerificationRequest}>Send Verification Email</button>
                <input
                    type="text"
                    placeholder="Enter verification code"
                    value={verificationCode}
                    onChange={(e) => setVerificationCode(e.target.value)}
                    required
                />
                <button onClick={verifyCode}>Verify Code</button>                />
            </div>
            {error && <div className="error-message">{error}</div>}
            <div className="footer">
                <span className="footer-text">
                    By clicking continue, you agree to our Terms of Service and Privacy Policy
                </span>
            </div>
            <div className="footer-links">
                <a href="/inquiry" className="auth-link">문의하기</a>
                <a href="/privacy-policy" className="auth-link">개인정보 처리방침</a>
                <a href="/terms-and-conditions" className="auth-link">이용약관</a>
            </div>
        </div>
    );
}

export default ConfirmEmail;
