import React, { useState } from 'react';
import './css/LoginForm.css';

function Authentication({ onLogin }) {
  const [code, setCode] = useState('');
  const [email, setEmail] = useState(''); // You'll need to pass this from the previous page or manage via context/global state
  const [userId, setUserId] = useState(''); // Same as above, needs to be managed based on app architecture

  const handleSubmit = async (event) => {
    event.preventDefault();

    const payload = {
      email: email,
      code: code,
      id: userId
    };

    try {
      const response = await fetch('/users/organization/check_auth_code', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(payload)
      });

      const data = await response.json();

      if (data.status === 0) {
        onLogin();  // Assuming onLogin will handle successful authentication
        alert('인증 성공!');  // Optionally alert the user or handle differently
      } else {
        alert('인증 실패: ' + data.error_msg);  // Alert the error message from the response
      }
    } catch (error) {
      alert('서버와의 통신에 실패했습니다. 다시 시도해주세요.');
    }
  };

  return (
    <div className="login-container">
      <div className="title-container">
        <h1 className="title">Everytime</h1>
        <p className="subtitle">for Foreign</p>
      </div>
      <div>
        <h3 style={{textAlign:'center',margin:0}}>인증번호가 학교이메일에 도착했습니다!</h3>
        <h5 style={{marginTop:0,textAlign:'center'}}>학교 이메일에서 인증번호를 확인해주세요!</h5>
        </div>
      <form onSubmit={handleSubmit} className="login-form"> 
        <input
          type="text"  // Changed type to "text" as "Authentication" is not a valid input type
          className="login-input"
          placeholder="인증번호를 입력해주세요!(제한시간: 5분)"
          value={code}
          onChange={(e) => setCode(e.target.value)}
          required
        />
        <button type="submit" className="login-button">
          인증번호 확인!
        </button>
      </form>
      <div className="links-container">
      </div>
      <div className="footer">
        <span className="footer-text">
          *인증번호가 틀리다면 학교이메일이 정확한지 확인해주세요!
        </span>
      </div>
      <div className="footer-links">
      </div>
    </div>
  );
}

export default Authentication;
